START TRANSACTION;
CREATE TABLE schema_migrations (version varchar(255) NOT NULL);
CREATE TABLE elements (id INTEGER PRIMARY KEY AUTO_INCREMENT NOT NULL, page_id integer, text varchar(255), created_at datetime, updated_at datetime);
CREATE TABLE reservations (id INTEGER PRIMARY KEY AUTO_INCREMENT NOT NULL, name varchar(255), email varchar(255), phone varchar(255), date date, time varchar(255), number_of_people integer, created_at datetime, updated_at datetime, site_id integer);
CREATE TABLE datafiles (id INTEGER PRIMARY KEY AUTO_INCREMENT NOT NULL, created_at datetime, updated_at datetime);
CREATE TABLE galleries (id INTEGER PRIMARY KEY AUTO_INCREMENT NOT NULL, name varchar(255), created_at datetime, updated_at datetime, position integer, site_id integer);
CREATE TABLE photos (id INTEGER PRIMARY KEY AUTO_INCREMENT NOT NULL, name varchar(255), gallery_id integer, position integer, created_at datetime, updated_at datetime, photo_file_name varchar(255), photo_content_type varchar(255), photo_file_size integer, photo_updated_at datetime, caption varchar(255));
CREATE TABLE users (id INTEGER PRIMARY KEY AUTO_INCREMENT NOT NULL, name varchar(255), email varchar(255), created_at datetime, updated_at datetime, encrypted_password varchar(255), salt varchar(255), remember_token varchar(255), status varchar(255), site_id integer);
CREATE TABLE opening_times (id INTEGER PRIMARY KEY AUTO_INCREMENT NOT NULL, day varchar(255), open time, close time, created_at datetime, updated_at datetime, info_id integer, site_id integer);
CREATE TABLE pages (id INTEGER PRIMARY KEY, name varchar(255), created_at datetime, updated_at datetime, content TEXT, site_id integer);
CREATE TABLE offers (id INTEGER PRIMARY KEY, title varchar(255), description TEXT, start_date varchar(255), end_date varchar(255), created_at datetime, updated_at datetime, photo_file_name varchar(255), photo_content_type varchar(255), photo_file_size integer, photo_updated_at datetime, conditions TEXT, position integer, site_id integer);
CREATE TABLE people (id INTEGER PRIMARY KEY, name varchar(255), role varchar(255), profile TEXT, photo_file_name varchar(255), photo_content_type varchar(255), photo_file_size integer, photo_updated_at datetime, created_at datetime, updated_at datetime, position integer, site_id integer);
CREATE TABLE testimonials (id INTEGER PRIMARY KEY, testimonial TEXT, name varchar(255), created_at datetime, updated_at datetime, position integer, site_id integer);
CREATE TABLE menus (id INTEGER PRIMARY KEY AUTO_INCREMENT NOT NULL, name varchar(255), created_at datetime, updated_at datetime, position integer, site_id integer, menu_type varchar(255), parent_id integer);
CREATE TABLE dishes (id INTEGER PRIMARY KEY AUTO_INCREMENT NOT NULL, menu_id integer, name varchar(255), description varchar(255), price integer(255), created_at datetime, updated_at datetime, position integer, photo_file_name varchar(255), photo_content_type varchar(255), photo_file_size integer, photo_updated_at datetime, parent_id integer);
CREATE TABLE wines (id INTEGER PRIMARY KEY AUTO_INCREMENT NOT NULL, name varchar(255), price integer(255), small integer(255), large integer(255), carafe integer(255), created_at datetime, updated_at datetime, category varchar(255), menu_id integer, position integer);
CREATE TABLE orders (id INTEGER PRIMARY KEY AUTO_INCREMENT NOT NULL, customer_id integer, time varchar(255), date date, created_at datetime, updated_at datetime, site_id integer);
CREATE TABLE customers (id INTEGER PRIMARY KEY AUTO_INCREMENT NOT NULL, name varchar(255), address1 varchar(255), address2 varchar(255), city varchar(255), postcode varchar(255), phone varchar(255), email varchar(255), encrypted_password varchar(255), salt varchar(255), remember_token varchar(255), created_at datetime, updated_at datetime, site_id integer);
CREATE TABLE items (id INTEGER PRIMARY KEY AUTO_INCREMENT NOT NULL, dish_id integer, quantity integer, order_id integer, created_at datetime, updated_at datetime);
CREATE TABLE sites (id INTEGER PRIMARY KEY AUTO_INCREMENT NOT NULL, name varchar(255), domain varchar(255), theme varchar(255), created_at datetime, updated_at datetime, phone varchar(255), address varchar(255), content text, directions text, latlong varchar(255), menus_template integer, checkout_template integer, galleries_template integer, homepage_template integer, location_template integer, offers_template integer, people_template integer, reservations_template integer, testimonials_template integer, dishes_template integer, orders_template integer);
CREATE UNIQUE INDEX unique_schema_migrations ON schema_migrations (version);
CREATE UNIQUE INDEX index_users_on_email ON users (email);
CREATE INDEX index_users_on_remember_token ON users (remember_token);
COMMIT;
